import { View, Text, Modal, TouchableOpacity, StyleSheet } from "react-native";
import { useState } from "react";
import { useNavigation } from "@react-navigation/native";
import { COLORS, SIZES, icons } from "../../constants";

import CreateEvent from "../../screens/Create/createEvent";
import Icons from "../Image/icons";

export default function BottomSheet() {
  const [isModalVisible, setModalVisible] = useState(false);

  const navigation = useNavigation();

  const goToCreateEvent = () => {
    // Navigate to the CreateEvent screen
    setModalVisible(false);
    navigation.push("CreateEvent");
  };
  const goToCreateCocktail = () => {
    // Navigate to the CreateEvent screen
    setModalVisible(false);
    navigation.push("CreateCocktail");
  };
  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };
  return (
    <View style={{ flex: 1 }}>
      <Modal
        transparent={true}
        visible={isModalVisible}
        animationType="fade"
        onRequestClose={closeModal}
      >
        <View
          style={{
            position: "absolute",
            top: 0,
            bottom: 0,
            left: 0,
            right: 0,
            backgroundColor: "rgba(0, 0, 0, 0.5)",
          }}
        />
      </Modal>
      <Modal
        transparent={true}
        visible={isModalVisible}
        animationType="slide"
        onRequestClose={closeModal}
      >
        <View
          style={{ flex: 1, position: "absolute", bottom: 0, width: "100%" }}
        >
          <View
            style={{
              height: 300,
              backgroundColor: COLORS.primaryColour1,
              padding: 20,
              borderTopEndRadius: 20,
              borderTopStartRadius: 20,
            }}
          >
            <View style={styles.headerContainer}>
              <Text
                style={{ fontSize: SIZES.xxLarge, color: COLORS.fontColour }}
              >
                Create
              </Text>
              <TouchableOpacity onPress={closeModal}>
                <Icons src={icons.close} dimension={30} />
              </TouchableOpacity>
            </View>
            <View style={{ paddingTop: 10 }}>
              <TouchableOpacity
                onPress={goToCreateEvent}
                style={styles.createContainer}
              >
                <Icons
                  src={icons.event}
                  dimension={25}
                  style={{ marginRight: 8 }}
                />
                <Text
                  style={{ fontSize: SIZES.medium, color: COLORS.fontColour }}
                >
                  New event
                </Text>
              </TouchableOpacity>

              <TouchableOpacity
                onPress={goToCreateCocktail}
                style={styles.createContainer}
              >
                <Icons
                  src={icons.cocktail}
                  dimension={30}
                  style={{ marginRight: 8 }}
                />
                <Text
                  style={{ fontSize: SIZES.medium, color: COLORS.fontColour }}
                >
                  New recipe
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>

      <TouchableOpacity
        onPress={openModal}
        activeOpacity={1}
        style={{
          position: "absolute",
          bottom: 20,
          right: 15,
          justifyContent: "center",
          alignItems: "center",
          alignContent: "center",
        }}
      >
        <View
          style={{
            backgroundColor: COLORS.primaryColour1,
            padding: 10,
            borderRadius: 30,
            width: 50,
            borderColor: COLORS.primaryColour2,
            borderWidth: 2,
          }}
        >
          <Text
            style={{
              color: "white",
              fontSize: 20,
              textAlign: "center",
              lineHeight: 25,
            }}
          >
            +
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 16, // Adjust as needed
    paddingVertical: 8, // Adjust as needed
  },
  createContainer: {
    flexDirection: "row",
    alignItems: "center",
    paddingTop: 10,
  },
});
