import React, { useState, useRef, useEffect } from "react";
import { StyleSheet, View, TouchableOpacity, TextInput } from "react-native";

import { COLORS, icons } from "../../constants";
import { useNavigation } from "@react-navigation/native";
import Icons from "../Image/icons";
import FilterItemsDiscover from "../../screens/Discover/FilterItemsDiscover";
import Spacer from "../Spacer/spacer";
export default function SearchIcon() {
  const [searchText, setSearchText] = useState("");

  const navigation = useNavigation();

  const inputRef = useRef(null);

  const handleGoBack = () => {
    navigation.navigate("Discover");
  };

  const handleTextInputChange = (text) => {
    setSearchText(text);
  };

  useEffect(() => {
    // Focus on the input field when the component mounts
    inputRef.current.focus();
  }, []);

  return (
    <View style={styles.container}>
      <Spacer horizontal={60} vertical={20}>
        <View style={styles.wrapper}>
          <TouchableOpacity onPress={handleGoBack} style={styles.backButton}>
            <Icons src={icons.backArrow} dimension={20} />
          </TouchableOpacity>
          <TextInput
            ref={inputRef}
            style={styles.input}
            placeholder="Enter username"
            placeholderTextColor="#80848A"
            onChangeText={(text) => setSearchText(text)}
            value={searchText}
          />
        </View>
        <View style={styles.filterItemsWrapper}>
          <FilterItemsDiscover />
        </View>
      </Spacer>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    backgroundColor: "black",
    height: "100%",
  },
  wrapper: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    backgroundColor: COLORS.primaryColour1,
    borderRadius: 20,
    flex: 1,
    marginLeft: 0,
    paddingHorizontal: 8,
    color: "#FFF",
  },
  backButton: {
    paddingRight: 20,
  },
  filterItemsWrapper: {
    flex: 1,
  },
});
