import React, { useState, useRef } from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import { BottomSheetModalProvider } from '@gorhom/bottom-sheet';

import { useNavigation } from '@react-navigation/native';
import { COLORS, SIZES } from '../../constants';

import CreateEvent from '../../screens/Create/createEvent';

export default function CustomTabBarButton({ accessibilityState, children, onPress }) {
  const [isModalVisible, setModalVisible] = useState(false);
  const bottomSheetModalRef = useRef(null);
  const snapPoints = [300]; // adjust the height of your bottom sheet
  const navigation = useNavigation();

  const goToCreateEvent = () => {
    setModalVisible(false);
    navigation.push('CreateEvent');
  };

  const goToCreateCocktail = () => {
    setModalVisible(false);
    navigation.push('CreateCocktail');
  };

  const openBottomSheet = () => {
    bottomSheetModalRef.current?.present();
  };

  return (
    <BottomSheetModalProvider>
      <View style={{ flex: 1 }}>
        <BottomSheetModal
          ref={bottomSheetModalRef}
          index={0}
          snapPoints={snapPoints}
          backgroundComponent={({ style }) => <View style={[style, { backgroundColor: 'rgba(0, 0, 0, 0.5)' }]} />}
        >
          <View style={{ height: 300, backgroundColor: COLORS.primaryColour1, padding: 20 }}>
            <Text style={{ fontSize: SIZES.xxLarge, color: COLORS.fontColour }}>Create</Text>
            <View style={{ paddingTop: 10 }}>
              <TouchableOpacity onPress={goToCreateEvent} style={{ paddingTop: 10 }}>
                <Text style={{ fontSize: SIZES.medium, color: COLORS.fontColour }}>New event</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={goToCreateCocktail} style={{ paddingTop: 10 }}>
                <Text style={{ fontSize: SIZES.medium, color: COLORS.fontColour }}>New recipe</Text>
              </TouchableOpacity>
            </View>
            <TouchableOpacity onPress={() => bottomSheetModalRef.current?.dismiss()}>
              <Text style={{ fontSize: SIZES.medium, color: COLORS.fontColour, top: 50 }}>Close</Text>
            </TouchableOpacity>
          </View>
        </BottomSheetModal>

        <TouchableOpacity
          onPress={openBottomSheet}
          activeOpacity={1}
          style={{
            position: 'absolute',
            bottom: 20,
            right: 15,
            justifyContent: 'center',
            alignItems: 'center',
            alignContent: 'center',
          }}
        >
          <View style={{ backgroundColor: COLORS.primaryColour1, padding: 10, borderRadius: 30, width: 50, borderColor: COLORS.primaryColour2, borderWidth: 2 }}>
            <Text style={{ color: 'white', fontSize: 20, textAlign: 'center', lineHeight: 25 }}>+</Text>
          </View>
        </TouchableOpacity>
      </View>
    </BottomSheetModalProvider>
  );
}
