import React, { useEffect, useRef, useState } from 'react';
import { Text, StyleSheet, View, TouchableOpacity, TextInput, FlatList } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { COLORS, icons } from '../../constants';
import Icons from '../Image/icons';
import FilterItems from './FilterItems';
import { useNavigation } from "@react-navigation/native";

export default function Search() {
  const [searchText, setSearchText] = useState('');

  const navigation = useNavigation();

  const inputRef = useRef(null);

  const data = [
    { id: '1', value: 'Rum' },
    { id: '2', value: 'Cola' },
    { id: '3', value: 'Vodka' },
    // Add more values as needed
  ];

const filterData = [
    { id: '1', title: 'Summer Drinks', value: 'Summer Drinks' },
    { id: '2', title: 'Winter Drinks', value: 'Winter Drinks' },
    { id: '3', title: 'Beach Drinks', value: 'Beach Drinks' },
    // Add more objects as needed
  ];

  const handleGoBack = () => {
    navigation.navigate('Home');
  };

  const handleValuePress = (value) => {
    setSearchText(value);
  };

  const handleTextInputChange = (text) => {
    setSearchText(text);
  };
  useEffect(() => {
    // Focus on the input field when the component mounts
    inputRef.current.focus();
  }, []);

  const renderItem = ({ item }) => (
    <TouchableOpacity onPress={() => handleValuePress(item.value)} style={styles.resultContainer}>
      <Text style={{ color: 'white' }}>{item.value}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <LinearGradient colors={['black', 'black']} style={styles.background}></LinearGradient>
      <View style={styles.wrapper}>
        <TouchableOpacity onPress={handleGoBack} style={styles.backButton}>
          <Icons src={icons.backArrow} dimension={20} />
        </TouchableOpacity>
        <TextInput
          ref={inputRef}
          style={styles.input}
          placeholder="Enter username"
          placeholderTextColor="#80848A"
          onChangeText={(text) => setSearchText(text)}
          value={searchText}
        />
      </View>
      <View style={styles.filterItems}>
          <FilterItems data={filterData} onSelect={handleTextInputChange} />
    </View>
         <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        style={styles.flatList}
        onSelect={handleTextInputChange} 
      /> 
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  wrapper: {
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingTop: 60,
    paddingHorizontal: 20,
  },
  background: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    height: '100%',
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    backgroundColor: COLORS.primaryColour1,
    borderRadius: 20,
    flex: 1,
    marginLeft: 0,
    paddingHorizontal: 8,
    color: '#FFF',
  },
  backButton: {
    paddingRight: 20,
  },
  resultContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginVertical: 8,
    padding: 20,
    width: '100%',
  },
  flatList: {
    width: '100%',
  },
  filterItems: {
    height: 100,
  }
});
