import { Text, StyleSheet, View, Image, Button } from 'react-native'
import { COLORS } from '../../constants'
import { useState, useEffect } from 'react'
import { supabase } from '../../config/supabaseConfig'
import * as ImagePicker from 'expo-image-picker';

export default function ProfilPicture({ src, dimension, focused, url, onUpload }) {
   const [uploading, setUploading] = useState(false)
  const [avatarUrl, setAvatarUrl] = useState(null)
  const avatarSize = { height: dimension, width: dimension }

   useEffect(() => {
    if (url) downloadImage(url)
   }, [url])

  async function downloadImage(path) {
    try {
      const { data, error } = await supabase.storage.from('avatars').download(path)

      if (error) {
        throw error
      }

      const fr = new FileReader()
      fr.readAsDataURL(data)
      fr.onload = () => {
        setAvatarUrl(fr.result)
      }
    } catch (error) {
      if (error instanceof Error) {
        console.log('Error downloading image: ', error.message)
      }
    }
  }
  
  async function uploadAvatar() {
    try {
      setUploading(true);

      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [1, 1],
        quality: 1,
      });

      if (result.cancelled) {
        console.warn('Image picker cancelled');
        return;
      }

      const { uri, type, name } = result;

      const formData = new FormData();
      formData.append('file', { uri, type, name });

      const fileExt = name.split('.').pop();
      const filePath = `${Math.random()}.${fileExt}`;

      const { error } = await supabase.storage.from('avatars').upload(filePath, formData);

      if (error) {
        throw error;
      }

      onUpload(filePath);
    } catch (error) {
      if (isCancel(error)) {
        console.warn('cancelled');
        // User cancelled the picker, exit any dialogs or menus and move on
      } else if (isInProgress(error)) {
        console.warn('multiple pickers were opened, only the last will be considered');
      } else if (error instanceof Error) {
        Alert.alert(error.message);
      } else {
        throw error;
      }
    } finally {
      setUploading(false);
    }
  }

    const styles = StyleSheet.create({
        image: {
            borderRadius: dimension / 2,
            width: dimension, 
            height: dimension, 
            borderColor: COLORS.fontColour,
            borderWidth: focused ? 1 : 0,
        },
    })
    
    return (
      <View >
        {avatarUrl ? (
        <Image
          source={{ uri: avatarUrl }}
          accessibilityLabel="Avatar"
          style={[avatarSize, styles.avatar, styles.image]}
        />
      ) : (
        <View style={[avatarSize, styles.avatar, styles.noImage]} />
      )}
        <Image
          source={{ uri: avatarUrl }}
          accessibilityLabel="Avatar"
          style={styles.image} />
         <View>
        <Button
          title={uploading ? 'Uploading ...' : 'Upload'}
          onPress={uploadAvatar}
          disabled={uploading}
        />
      </View>
      </View>
    )
}
