import React, {useEffect, useRef, useState } from 'react';
import { Text, StyleSheet, View, TouchableOpacity, TextInput, Image, FlatList } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { COLORS, icons, images } from '../../constants';
import { useNavigation } from '@react-navigation/native';
import Icons from "../Image/icons";

export default function Search({ data, onSelect  }) {

  const renderItem = ({ item }) => (
      <TouchableOpacity onPress={() => onSelect(item.value)} style={styles.item}>
      <LinearGradient
        colors={[COLORS.accentColour1Orange, COLORS.accentColour3Blue]}
        style={styles.itemGradient}
        start={{ x: 0, y: 2 }}
        end={{ x: 1, y: 0.5 }}
      >
        <Text style={styles.itemText}>{item.title}</Text>
      </LinearGradient>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        style={styles.flatList}
        horizontal
        showsHorizontalScrollIndicator={false}
      />
    </View>
  );
};

const styles = StyleSheet.create({
    container: {
    flex: 1,
        padding: 16,
  },
  flatList: {
      marginTop: 16,

  },
    item: {
    marginHorizontal: 20,
    borderRadius: 30,
    height: 50,
    overflow: 'hidden',
    borderColor: COLORS.primaryColour2,
    borderWidth: 1,
    },
    itemGradient: {
    padding: 16,
    width: 130,
    height: 50,
    borderRadius: 30,
    justifyContent: 'center',
    
  },
    itemText: {
        color: COLORS.fontColour,
        textAlign: 'center',
        fontSize: 12,
        fontWeight: 'bold',
  }
});
