import React, { useState, useRef, useEffect } from "react";
import {
  StyleSheet,
  View,
  TouchableOpacity,
  TextInput,
  Text,
  ScrollView,
  FlatList,
  Image,
} from "react-native";

import { COLORS, icons, images } from "../../constants";
import { useNavigation } from "@react-navigation/native";
import Icons from "../Image/icons";
import FilterItemsDiscover from "../../screens/Discover/FilterItemsDiscover";
import Spacer from "../Spacer/spacer";
export default function SearchIcon() {
  const [searchText, setSearchText] = useState("");

  const navigation = useNavigation();

  const inputRef = useRef(null);

  const handleGoBack = () => {
    navigation.navigate("Discover");
  };

  const handleTextInputChange = (text) => {
    setSearchText(text);
  };

  useEffect(() => {
    // Focus on the input field when the component mounts
    inputRef.current.focus();
  }, []);

  const data = [
    { id: 1, image: images.CocktailImage, text: "Summer drink" },
    { id: 2, image: images.CocktailImage, text: "Winter Drink" },
    { id: 3, image: images.CocktailImage, text: "Text 3" },
    { id: 4, image: images.CocktailImage, text: "Text 4" },
    { id: 5, image: images.CocktailImage, text: "Text 5" },
    { id: 6, image: images.CocktailImage, text: "Text 6" },
    { id: 7, image: images.CocktailImage, text: "Text 7" },
    { id: 8, image: images.CocktailImage, text: "Text 8" },
    { id: 9, image: images.CocktailImage, text: "Text 9" },
  ];

  return (
    <View style={styles.container}>
      <Spacer horizontal={30} vertical={60}>
        {/* First row: Back button and input */}
        <View style={styles.firstRow}>
          <TouchableOpacity onPress={handleGoBack} style={styles.backButton}>
            <Icons src={icons.backArrow} dimension={20} />
          </TouchableOpacity>
          <TextInput
            ref={inputRef}
            style={styles.input}
            placeholder="Enter username"
            placeholderTextColor="#80848A"
            onChangeText={(text) => setSearchText(text)}
            value={searchText}
          />
        </View>

        {/* Second row: FilterItemsDiscover */}
        <View style={styles.filterItemsWrapper}>
          <FilterItemsDiscover />
        </View>

        {/* Third row: Scrollable view */}
        <View style={styles.scrollViewContainer}>
          <FlatList
            data={data}
            keyExtractor={(item) => item.id.toString()}
            renderItem={({ item }) => (
              <View style={styles.imageContainer}>
                <Image source={item.image} style={styles.image} />
                <Text style={styles.text}>{item.text}</Text>
              </View>
            )}
          />
        </View>
      </Spacer>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    backgroundColor: "black",
    height: "100%",
  },
  wrapper: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  firstRow: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },
  input: {
    flex: 1,
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    backgroundColor: COLORS.primaryColour1,
    borderRadius: 20,
    flex: 1,
    marginLeft: 0,
    paddingHorizontal: 8,
    color: "#FFF",
  },
  backButton: {
    paddingRight: 20,
  },
  filterItemsWrapper: {
    flex: 0.2,
  },
  scrollViewContainer: {
    flex: 1,
    width: "100%",
  },
  scrollViewGrid: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },
  resultText: {
    color: COLORS.fontColour,
    fontSize: 16,
    lineHeight: 24,
    marginVertical: 20,
  },
  image: {
    width: "100%",
    height: "100%",
    resizeMode: "cover",
  },
});
