import React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";

import CreateEvent from "../screens/Create/createEvent";
import CreateCocktail from "../screens/Create/createCocktail";
import MainTabNavigator from "./MainTabNavigator";
import { COLORS } from "../constants";

const Stack = createNativeStackNavigator();

export default function UserStack() {
  return (
    
      <NavigationContainer>
      <Stack.Navigator
       screenOptions={{ headerShown: false }}
      >
        {/* Bottom Tab Navigator */}
        <Stack.Screen name="Main" component={MainTabNavigator}/>

        {/* Screens not part of the bottom navigation */}
        <Stack.Screen name="CreateEvent" component={CreateEvent}
          options={{
            presentation: 'transparentModal',
            animationTypeForReplace: "pop",
            headerShown: true,
             headerStyle: {
              backgroundColor: COLORS.heroColour,
            },
              headerTintColor: COLORS.fontColour,
              headerTitleStyle: {
                fontWeight: 'bold',
              },
        }} />
        <Stack.Screen name="CreateCocktail" component={CreateCocktail}
          options={{
            presentation: 'transparentModal',
            animationTypeForReplace: "pop",
            headerShown: true,
        }} />
      </Stack.Navigator>
    </NavigationContainer>
  
  );
}
