import React, { useEffect, useState } from "react";
import { Text, StyleSheet, View } from "react-native";
import { Button } from "react-native-elements";
import { LinearGradient } from "expo-linear-gradient";
import { useSession } from "../../hooks/useSession";
import { supabase } from "../../config/supabaseConfig";

import ProfilPictureUplaod from "../../components/ProfileImage/ProfilPictureUplaod";
import { COLORS, SIZES, images } from "../../constants";
import CTAButton from "../../components/Basics/CTAButton/CTAButton";
import Spacer from "../../components/Basics/Spacer/spacer";
import Background from "../../components/Basics/Image/Background";

export default function Profile() {
  const { session } = useSession();
  // console.log(session.user.user_metadata.username);
  const [profile, setProfile] = useState(null);

  function goToEditProfile() {}

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const { data, error } = await supabase
          .from("profiles")
          .select("username")
          .eq("id", session.user.id)
          .single();

        if (error) {
          console.error("Error fetching profile:", error.message);
          return;
        }

        setProfile(data);
      } catch (error) {
        console.error("Error fetching profile:", error.message);
      }
    };

    fetchProfile();
  }, [session]);

  if (!session || !session.user) {
    console.warn("Session or user not available:", session);
    return null;
  }

  const { user } = session;

  return (
    <View style={styles.container}>
      <Background src={images.Background} style={styles.backgroundImage} />

      {/* <LinearGradient
        colors={[COLORS.heroColour, "black"]}
        style={styles.background}
      ></LinearGradient> */}
      <View style={styles.wrapper}>
        <ProfilPictureUplaod dimension={150} />
        <View style={styles.profileInfo}>
          {profile && (
            <>
              <Text style={styles.usernameText}>
                Username: {profile.username}
              </Text>
              <Spacer vertical={40} horizontal={0}>
                <View style={styles.containerInfo__user}>
                  <View style={styles.containerInfo__user__grid}>
                    <Text style={styles.numberText}>
                      2{/* {profile.eventsCreated} */}
                    </Text>
                    <Text style={styles.statusText}>Event Created</Text>
                  </View>

                  <View style={styles.containerInfo__user__grid}>
                    <Text style={styles.numberText}>
                      3{/* {profile.eventsAttending} */}
                    </Text>
                    <Text style={styles.statusText}>Event Attending</Text>
                  </View>
                </View>
              </Spacer>
            </>
          )}
        </View>
        <CTAButton
          title={"Edit Profile"}
          onPress={goToEditProfile}
          variant={"primary"}
          height={60}
          width={300}
        />
        <Button
          title="Sign Out"
          onPress={() => supabase.auth.signOut()}
          buttonStyle={styles.signOutButton}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
  },
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    position: "absolute",
    width: "100%",
    height: "100%",
  },
  containerInfo__user: {
    flexDirection: "row",
    justifyContent: "space-around",
    gap: 60,
  },
  containerInfo__user__grid: {
    alignItems: "center",
  },
  numberText: {
    fontSize: 18,
    fontWeight: "bold",
    color: COLORS.fontColour,
  },
  statusText: {
    fontSize: 18,
    color: COLORS.fontColour,
  },

  wrapper: {
    width: "100%",
    height: "100%",
    alignItems: "center",
    paddingTop: 30,
  },
  background: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    height: "100%",
  },
  profileInfo: {
    alignItems: "center",
  },
  usernameText: {
    fontSize: SIZES.medium,
    color: COLORS.fontColour,
  },
});
