  heroColour: "#002B48",
  accentColour1Orange: "#FC5C00",
  accentColour2Pink: "#FF4187",
  accentColour3Blue: "#00A1D6",

  fontColour: "#DDDDD9",
  primaryColour2: "#80848A",
  primaryColour1: "#424448",

  grey: "#2a2c2e",
  black: "#000000",
  success: "#80C41C",
  danger: "#C11E1E",
  warning: "#F5BD08",
};

const FONT = {
  regular: "DMRegular",
  medium: "DMMedium",
  bold: "DMBold",
};

const SIZES = {
  xSmall: 10,
  small: 12,
  medium: 16,
  large: 20,
  xLarge: 24,
  xxLarge: 32,
};

export { COLORS, FONT, SIZES };
