import { Text, StyleSheet, View , TouchableOpacity, TextInput} from 'react-native'
import React, { useState, useEffect } from 'react';
import { useNavigation } from '@react-navigation/native';
import { LinearGradient } from 'expo-linear-gradient';
import { COLORS } from '../../constants';

export default function Search() {
     const navigation = useNavigation();
   const handleGoBack = () => {
    navigation.navigate("Home");
  };

  return(
    <View style={styles.container}> 
      <LinearGradient
        colors={[COLORS.heroColour, "black"]}
        style={styles.background}
      ></LinearGradient>
       <View style={styles.wrapper}>
       <TouchableOpacity onPress={handleGoBack}>
       <Text style={styles.goBackButtonText}>Go Back</Text>
      </TouchableOpacity>
      <Text style={styles.title}>Search User</Text>
      <TextInput
        style={styles.input}
       placeholder="Enter username"
       placeholderTextColor="#80848A"
        onChangeText={(text) => {
        }}
        />
        </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  wrapper: {
    width: "100%",
    height: "100%",
    alignItems: 'center', 
    paddingTop: 50,
  },
   background: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    height: "100%",
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
    color: "#FC5C00",
  },
  keyboardAvoidingContainer: {
    flex: 1,
    width: '100%',
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    width: '80%',
    marginBottom: 16,
    paddingHorizontal: 8,
    color: "#FFF",
  },
  resultContainer: {
    marginVertical: 8,
  },
  resultContainer: {
    flex: 1,
    flexDirection: "row",
    gap: 20,
    backgroundColor: "#424448",
    marginVertical: 8,
    padding: 20,
    width: "100%",
  },
  noResultsText: {
    fontSize: 18,
    marginTop: 16,
    color: 'red',
  },
  goBackButtonText: {
    color: "#fff"
  }
});
